      <Dialog open={showPayments} onClose={() => setShowPayments(false)}>
        <DialogTitle align='center'>ÿ™ŸàŸÖÿßÿ±⁄©ÿ±ŸÜÿß Ÿæÿßÿ±€ïÿØÿßŸÜ€é</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            type="number"
            label="Amount Paid"
            value={paymentAmount}
            onChange={(e) => setPaymentAmount(e.target.value)}
            sx={{ mt: 1 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowPayments(false)} style={{ marginRight: '50px' }}>‚ùå Cancel</Button>
          <Button onClick={registerPayment} variant="contained">üíæ Save</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

