import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import { Box, Grid, TextField, Button, Typography } from '@mui/material';



const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('❌ Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
         <button
          className="btn btn-secondary"
          onClick={() => navigate(`/customer/${customerId}`)}
        >
          🔙 زفرین
        </button> 
        <h2>{!itemId || itemId === 'new' ? '➕ زێدەکرنا کەل و پەلا' : '✏️ Edit Product'}</h2>
        
      </div>

      <form dir="rtl" onSubmit={handleSubmit}>
  <Box sx={{ backgroundColor: '#f8f9fa', p: 4, borderRadius: 2, boxShadow: 2 }}>

    <Typography variant="h6" gutterBottom>
      زانیاری کەل و پەلا
    </Typography>

    <Grid container spacing={2}>

      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          label="ناف"
          name="name"
          placeholder="نافێ کەل و پەلا"
          value={form.name}
          onChange={handleChange}
          required
        />
      </Grid>

      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          label="وەسف"
          name="description"
          placeholder="کورتکرنا وەسفێ"
          value={form.description}
          onChange={handleChange}
        />
      </Grid>

      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          label="ژمارە"
          name="quantity"
          type="number"
          inputProps={{ min: 1 }}
          value={form.quantity}
          onChange={handleChange}
          required
        />
      </Grid>

      <Grid item xs={12} md={6}>
        <TextField
          fullWidth
          label="بهایێ ئێکێ"
          name="unitPrice"
          type="number"
          inputProps={{ step: 0.01, min: 0 }}
          value={form.unitPrice}
          onChange={handleChange}
          required
        />
      </Grid>

      {preview && (
        <Grid item xs={12}>
          <Typography variant="subtitle1" gutterBottom>
            Preview
          </Typography>
          <Box>
            <img src={preview} alt="Preview" style={{ maxWidth: '200px', borderRadius: '10px' }} />
          </Box>
        </Grid>
      )}

      <Grid item xs={12} textAlign="end">
        <Button type="submit" variant="contained" color="primary">
          Save Product 💾
        </Button>
      </Grid>

    </Grid>
  </Box>
</form>
    </div>
  );
};

export default ItemForm;
