import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  useTheme,
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import './ItemForm.css';

const ItemForm = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const { customerId } = useParams();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: '',
    unitPrice: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // submit logic here
    alert("💾 پاشەکەوت کرا!");
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        backgroundColor: '#e0e0e0',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 2,
      }}
    >
      <Box
        sx={{
          width: '100%',
          maxWidth: 400,
          backgroundColor: '#fff',
          borderRadius: 6,
          p: 4,
          boxShadow: 3,
          position: 'relative',
        }}
      >
        {/* Back Button */}
        <Button
          variant="outlined"
          onClick={() => navigate(`/customer/${customerId}`)}
          sx={{
            position: 'absolute',
            top: 16,
            left: 16,
            color: '#007aff',
            borderColor: '#007aff',
            fontWeight: 'bold',
            borderRadius: 2,
          }}
        >
          Back
        </Button>

        <form onSubmit={handleSubmit} style={{ marginTop: 50 }}>
          <TextField
            label="ناوی کاڵا"
            name="name"
            value={form.name}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="وەسفی کاڵا"
            name="description"
            value={form.description}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="ژمارە"
            name="quantity"
            type="number"
            value={form.quantity}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <TextField
            label="نرخی یەکەم"
            name="unitPrice"
            type="number"
            value={form.unitPrice}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
          <Box textAlign="center" mt={3}>
            <Button
              type="submit"
              variant="contained"
              sx={{
                backgroundColor: '#007aff',
                color: 'white',
                px: 6,
                py: 1,
                fontSize: '1.2rem',
                borderRadius: 2,
                '&:hover': {
                  backgroundColor: '#005edb',
                },
              }}
            >
              Save
            </Button>
          </Box>
        </form>
      </Box>
    </Box>
  );
};

export default ItemForm;





