import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, Button, Stack, TextField, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Pagination } from '@mui/material';
import api from '../api';
import './CustomerList.css';

const ITEMS_PER_PAGE = 5;

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [selectedLetter, setSelectedLetter] = useState('');

  useEffect(() => {
    api.get('/customers').then((res) => {
      const customersWithBill = res.data.map((c, i) => ({
        ...c,
        billNo: 1000 + i + 1,
      }));
      setCustomers(customersWithBill);
    });
  }, []);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
    setPage(1);
  };

  const handleLetterClick = (letter) => {
    setSelectedLetter(letter);
    setPage(1);
  };

  const filteredCustomers = customers.filter((c) => {
    const lowerSearch = search.toLowerCase();
    const matchesSearch =
      c.name.toLowerCase().includes(lowerSearch) ||
      c.phone?.toLowerCase().includes(lowerSearch) ||
      c.email?.toLowerCase().includes(lowerSearch) ||
      c.address?.toLowerCase().includes(lowerSearch) ||
      c.billNo?.toString().includes(lowerSearch);

    const matchesLetter = selectedLetter === '' || c.name.startsWith(selectedLetter);
    return matchesSearch && matchesLetter;
  });

  const totalPages = Math.ceil(filteredCustomers.length / ITEMS_PER_PAGE);
  const visibleCustomers = filteredCustomers.slice(
    (page - 1) * ITEMS_PER_PAGE,
    page * ITEMS_PER_PAGE
  );

  const letters = Array.from(
    new Set(customers.map((c) => c.name[0]?.toUpperCase()).sort((a, b) => a.localeCompare(b)))
  );

  return (
    <Box p={2}>
      <Typography variant="h4" align="center" gutterBottom>
        ŸÑ€åÿ≥ÿ™ÿß ÿ®⁄©ÿ±ÿß üìã
      </Typography>

      <Stack
        direction={{ xs: 'column', sm: 'row' }}
        spacing={2}
        alignItems="stretch"
        justifyContent="flex-start"
        mb={3}
        sx={{
          p: 2,
          backgroundColor: 'background.paper',
          borderRadius: 3,
          boxShadow: 3,
          gap: 2,
          flexWrap: 'nowrap',
        }}
      >
        <TextField
          variant="outlined"
          size="medium"
          fullWidth
          placeholder="üîç ŸÑ€é⁄Ø€ïÿ±€åÿßŸÜÿß ŸÜÿßŸÅ, ÿ™€ïŸÑ€ïŸÅŸàŸÜ, ÿ¶€åŸÖ€ï€å⁄µ, ŸÜÿßŸÅŸàŸÜ€åÿ¥ÿßŸÜ, €åÿßŸÜ ⁄òŸÖÿßÿ±€ï€å Ÿæÿ≥ŸàŸÑ€ï"
          value={search}
          onChange={handleSearchChange}
          sx={{
            backgroundColor: '#fff',
            borderRadius: 2,
            '& .MuiOutlinedInput-root': {
              borderRadius: 2,
            },
            flex: 1,
            direction: 'rtl',
            textAlign: 'right',
          }}
        />
        <Button
        component={Link}
        to="/customer/new"
        variant="contained"
        color="primary"
        size="medium"
        className="customer-add-button"
      >
        ÿ≤€éÿØ€ï⁄©ÿ±ŸÜÿß ÿ®⁄©ÿ±€å ‚ûï
      </Button>


      </Stack>

      <TableContainer component={Paper} className="table-container" style={{ direction: 'rtl' }}>
        <Table className="customer-table">
          <TableHead style={{ textAlign: 'center' }}>
            <TableRow>
              <TableCell>ŸÜÿßŸÅ</TableCell>
              <TableCell>ÿ™€ïŸÑ€ïŸÅ€ÜŸÜ</TableCell>
              <TableCell>ŸÜÿßŸÅŸàŸÜ€åÿ¥ÿßŸÜ</TableCell>
              <TableCell>ÿ¶€åŸÖ€ï€å⁄µ</TableCell>
              <TableCell>⁄òŸÖÿßÿ±ÿß Ÿæÿ≥ŸàŸÑ€é</TableCell>
              <TableCell>⁄©ÿ±ÿØÿßÿ±€ï⁄©ÿßŸÜ</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {visibleCustomers.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  No customers found.
                </TableCell>
              </TableRow>
            ) : (
              visibleCustomers.map((c) => (
                <TableRow key={c._id}>
                  <TableCell style={{ textAlign: 'center' }}>{c.name}</TableCell>
                  <TableCell style={{ textAlign: 'center' }}>{c.phone}</TableCell>
                  <TableCell style={{ textAlign: 'center' }}>{c.address}</TableCell>
                   <TableCell style={{ textAlign: 'center' }}>{c.email}</TableCell>
                   <TableCell style={{ textAlign: 'center' }}>{c.billNo}</TableCell>
                  <TableCell>
                    <Button
                      component={Link}
                      to={`/customer/${c._id}`}
                      size="small"
                      variant="contained"
                    >
                      View
                    </Button>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {totalPages > 1 && (
        <Box mt={3} display="flex" justifyContent="center">
          <Pagination
            count={totalPages}
            page={page}
            onChange={(e, value) => setPage(value)}
            color="primary"
          />
        </Box>
      )}
    </Box>
  );
};

export default CustomerList;
