import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import { Button } from '@mui/material';
import KeyboardBackspaceIcon from '@mui/icons-material/KeyboardBackspace';



const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('‚ùå Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="container my-4">
        <Button onClick={() => navigate(`/customer/${customerId}`)} variant="contained" style={{ position: 'absolute', top: '20px', left: '30px' }}><KeyboardBackspaceIcon /> ÿ≤ŸÅÿ±€åŸÜ</Button>
      <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '100vh',  }}>
        
        <form
      dir="rtl"
      onSubmit={handleSubmit}
      className="bg-white p-5 rounded shadow"
      style={{ width: '100%', maxWidth: '600px', marginBottom: '-5px' }}
    >
      <h4 className="m-0" style={{ textAlign: 'center', marginBottom: '20px', color: 'blue' }}>
          {!itemId || itemId === 'new' ? 'ÿ≤€éÿØ€ï⁄©ÿ±ŸÜÿß ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑÿß ‚ûï' : '‚úèÔ∏è Edit Product'}
        </h4>
      <div className="mb-4 d-flex justify-content-between align-items-center">
      </div>

      <div className="mb-3">
        <label className="form-label">ŸÜÿßŸÅ:</label>
        <input
          name="name"
          className="form-control"
          placeholder="ŸÜÿßŸÅ€é ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑ€å"
          value={form.name}
          onChange={handleChange}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">Ÿà€ïÿ≥ŸÅ:</label>
        <input
          name="description"
          className="form-control"
          placeholder="⁄©Ÿàÿ±ÿ™⁄©ÿ±ŸÜÿß Ÿà€ïÿ≥ŸÅ€é"
          value={form.description}
          onChange={handleChange}
        />
      </div>

      <div className="mb-3">
        <label className="form-label">⁄òŸÖÿßÿ±€ï:</label>
        <input
          name="quantity"
          type="number"
          min="1"
          className="form-control"
          value={form.quantity}
          onChange={handleChange}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">ÿ®Ÿáÿß€å€é ÿ¶€é⁄©€é:</label>
        <input
          name="unitPrice"
          type="number"
          step="0.01"
          min="0"
          className="form-control"
          value={form.unitPrice}
          onChange={handleChange}
          required
        />
      </div>

      <div className="d-grid">
        <Button type="submit" variant="contained">üíæ Save Product</Button>
      </div>
    </form>
      </div>
    </div>
  );
};

export default ItemForm;
