import React, { useEffect, useState } from 'react';
import './CustomerDetail.css';
import { useParams, Link } from 'react-router-dom';
import api from '../api';

const CustomerDetail = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [items, setItems] = useState([]);
  const [showPayments, setShowPayments] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState('');

  const fetchCustomerAndItems = async () => {
    try {
      const customerRes = await api.get('/customers');
      const sortedCustomers = customerRes.data.sort((a, b) =>
        a.name.localeCompare(b.name, 'ku', { sensitivity: 'base' })
      );

      const foundIndex = sortedCustomers.findIndex(c => c._id === id);
      const foundCustomer = sortedCustomers[foundIndex];
      if (foundCustomer) {
        foundCustomer.billNo = 1000 + foundIndex + 1;
        setCustomer(foundCustomer);
      }

      const itemsRes = await api.get('/items');
      const filtered = itemsRes.data.items.filter(i =>
        i.customerId === id || i.customerId?._id === id
      );
      setItems(filtered);
    } catch (err) {
      console.error('Error fetching data:', err);
    }
  };

  useEffect(() => {
    fetchCustomerAndItems();
  }, [id]);

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      await api.delete(`/items/${itemId}`);
      fetchCustomerAndItems(); // Refetch to reflect latest
    }
  };

  const registerPayment = async () => {
    if (remaining <= 0) {
      alert('Customer has already paid in full.');
      return;
    }

    try {
      await api.post(`/customers/${customer._id}/payments`, {
        amount: parseFloat(paymentAmount)
      });
      setShowPayments(false);
      setPaymentAmount('');
      fetchCustomerAndItems(); // Refresh payment history
    } catch (err) {
      alert('Failed to save payment');
      console.error(err);
    }
  };

  if (!customer) return <p>Loading...</p>;

  const totalValue = items.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
  const totalPaid = customer.payments?.reduce((sum, p) => sum + p.amount, 0) || 0;
  const remaining = totalValue - totalPaid;

  const whatsappLink = `https://wa.me/${customer.phone?.replace(/\D/g, '')}?text=Hello%20${encodeURIComponent(customer.name)},%20regarding%20your%20inventory`;
  const mailLink = `mailto:${customer.email}?subject=Inventory Update`;

  return (
    <div className="item-detail">
      <Link to="/" className="button" style={{ float: 'left' }}>
        ← Back
      </Link>
      <br />
      <br />
      <h1>{customer.name}</h1>
      <p><strong>🧾 Bill No:</strong> {customer.billNo}</p>
      <p>📍 {customer.address || 'No address provided'}</p>
      <p>📞 {customer.phone || 'No phone provided'}</p>
      <p>✉️ {customer.email || 'No email provided'}</p>

      <div style={{ marginTop: '10px', display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {customer.phone && (
          <a href={whatsappLink} className="button" target="_blank" rel="noreferrer">💬 WhatsApp</a>
        )}
        {customer.email && <a href={mailLink} className="button">📧 Email</a>}
        <Link to={`/customer/${customer._id}/edit`} className="button">✏️ Edit Customer</Link>
        <Link to={`/customer/${customer._id}/receipt`} className="button">🧾 Print Receipt</Link>
        {remaining > 0 ? (
          <button className="button" onClick={() => setShowPayments(true)}>💸 Register Payment</button>
        ) : (
          <span style={{
            padding: '8px 14px',
            background: '#28a745',
            color: 'white',
            borderRadius: '6px',
            fontWeight: 'bold',
            fontSize: '14px'
          }}>
            ✅ Paid in Full
          </span>
        )}
      </div>

      <hr style={{ margin: '2rem 0' }} />
      <h2>📦 Products</h2>

      <Link to={`/customer/${customer._id}/item/new`} className="button" style={{ marginBottom: '1rem' }}>
        ➕ Add Product
      </Link>

      {items.length === 0 ? (
        <p>No items for this customer.</p>
      ) : (
        <>
          <table className="customer-table">
            <thead>
              <tr>
                <th>Image</th>
                <th>Product Name</th>
                <th>Quantity</th>
                <th>Unit Price</th>
                <th>Total</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map(item => (
                <tr key={item._id}>
                  <td>
                    {item.imageUrl ? (
                      <img
                        src={`http://localhost:5000${item.imageUrl}`}
                        alt={item.name}
                        style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '6px' }}
                      />
                    ) : '—'}
                  </td>
                  <td>{item.name}</td>
                  <td>{item.quantity}</td>
                  <td>${item.unitPrice}</td>
                  <td>${item.totalPrice.toFixed(2)}</td>
                  <td>
                    <Link to={`/customer/${customer._id}/item/${item._id}/edit`} className="button" style={{ marginRight: '5px' }}>
                      ✏️ Edit
                    </Link>
                    <button onClick={() => handleDeleteItem(item._id)} className="button" style={{ backgroundColor: 'red' }}>
                      🗑 Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div style={{ textAlign: 'right', fontWeight: 'bold', marginTop: '1rem' }}>
            🧾 Total Inventory Value: ${totalValue.toFixed(2)} <br />
            💳 Total Paid: ${totalPaid.toFixed(2)} <br />
            💰 Remaining: ${remaining.toFixed(2)}
            <details style={{ marginTop: '0.5rem' }}>
              <summary>📆 View Payment History</summary>
              <ul>
                {customer.payments?.map((p, idx) => (
                  <li key={idx}>💵 ${p.amount} on {new Date(p.date).toLocaleDateString()}</li>
                ))}
              </ul>
            </details>
          </div>
        </>
      )}


      {showPayments && (
        <div className="modal-backdrop">
          <div className="modal">
            <h3>Register Payment</h3>
            <input
              type="number"
              min="0"
              placeholder="Amount Paid"
              value={paymentAmount}
              onChange={(e) => setPaymentAmount(e.target.value)}
            />
            <div style={{ marginTop: '1rem' }}>
              <button className="button" onClick={registerPayment}>💾 Save Payment</button>
              <button onClick={() => setShowPayments(false)} className="button" style={{ backgroundColor: '#888', marginLeft: '10px' }}>
                ❌ Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomerDetail;
