import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Button,
  Grid,
  TextField,
  Typography,
  Paper,
} from '@mui/material';
import api from '../api';

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('‚ùå Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Button
          variant="outlined"
          color="secondary"
          onClick={() => navigate(`/customer/${customerId}`)}
        >
          üîô ÿ≤ŸÅÿ±€åŸÜ
        </Button>
        <Typography variant="h5">
          {!itemId || itemId === 'new' ? '‚ûï ÿ≤€éÿØ€ï⁄©ÿ±ŸÜÿß ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑÿß' : '‚úèÔ∏è ⁄Ø€Üÿ±ÿßŸÜ⁄©ÿßÿ±€å ŸÑ€ï ⁄©ÿß⁄µÿß'}
        </Typography>
      </Box>

      <Paper component="form" onSubmit={handleSubmit} sx={{ p: 3 }} elevation={3} dir="rtl">
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <TextField
              name="name"
              label="ŸÜÿßŸÅ"
              fullWidth
              required
              value={form.name}
              onChange={handleChange}
              placeholder="ŸÜÿßŸÅ€é ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑÿß"
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <TextField
              name="description"
              label="Ÿà€ïÿ≥ŸÅ"
              fullWidth
              value={form.description}
              onChange={handleChange}
              placeholder="⁄©Ÿàÿ±ÿ™⁄©ÿ±ŸÜÿß Ÿà€ïÿ≥ŸÅ€é"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              name="quantity"
              label="⁄òŸÖÿßÿ±€ï"
              type="number"
              inputProps={{ min: 1 }}
              fullWidth
              required
              value={form.quantity}
              onChange={handleChange}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              name="unitPrice"
              label="ÿ®Ÿáÿß€å€é ÿ¶€é⁄©€é"
              type="number"
              inputProps={{ min: 0, step: 0.01 }}
              fullWidth
              required
              value={form.unitPrice}
              onChange={handleChange}
            />
          </Grid>

          {/* <Grid item xs={12}>
            <Button variant="contained" component="label">
              ÿ®ÿßÿ±⁄©ÿ±ÿØŸÜÿß Ÿà€éŸÜ€ï
              <input hidden type="file" onChange={handleFileChange} />
            </Button>
          </Grid> */}

          {preview && (
            <Grid item xs={12}>
              <Typography variant="subtitle1">Ÿà€éŸÜ€ï€å Ÿæ€éÿ¥ÿ®€åŸÜ€å</Typography>
              <Box mt={1}>
                <img
                  src={preview}
                  alt="Preview"
                  style={{ maxWidth: '200px', borderRadius: '10px' }}
                />
              </Box>
            </Grid>
          )}
        </Grid>

        <Box mt={3} display="flex" justifyContent="flex-end">
          <Button type="submit" variant="contained" color="primary">
            üíæ Ÿæÿßÿ¥€ï⁄©€ïŸàÿ™⁄©ÿ±ŸÜ
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default ItemForm;




