import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, Button, Stack, TextField, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Pagination, } from '@mui/material';
import api from '../api';
import './CustomerList.css';

const ITEMS_PER_PAGE = 5;

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [selectedLetter, setSelectedLetter] = useState('');

  useEffect(() => {
    api.get('/customers').then((res) => {
      const customersWithBill = res.data.map((c, i) => ({
        ...c,
        billNo: 1000 + i + 1,
      }));
      setCustomers(customersWithBill);
    });
  }, []);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
    setPage(1);
  };

  const handleLetterClick = (letter) => {
    setSelectedLetter(letter);
    setPage(1);
  };

  const filteredCustomers = customers.filter((c) => {
    const lowerSearch = search.toLowerCase();
    const matchesSearch =
      c.name.toLowerCase().includes(lowerSearch) ||
      c.phone?.toLowerCase().includes(lowerSearch) ||
      c.email?.toLowerCase().includes(lowerSearch) ||
      c.address?.toLowerCase().includes(lowerSearch) ||
      c.billNo?.toString().includes(lowerSearch);

    const matchesLetter = selectedLetter === '' || c.name.startsWith(selectedLetter);
    return matchesSearch && matchesLetter;
  });

  const totalPages = Math.ceil(filteredCustomers.length / ITEMS_PER_PAGE);
  const visibleCustomers = filteredCustomers.slice(
    (page - 1) * ITEMS_PER_PAGE,
    page * ITEMS_PER_PAGE
  );

  const letters = Array.from(
    new Set(customers.map((c) => c.name[0]?.toUpperCase()).sort((a, b) => a.localeCompare(b)))
  );

  return (
    <Box p={2}>
      <Typography variant="h4" align="center" gutterBottom>
        📋 Customer List
      </Typography>

      <Stack direction="row" spacing={1} justifyContent="center">
  <Button
    variant="outlined"
    color="primary"
    size="small"
    onClick={() => handleView(row)}
    sx={{ borderRadius: 2, minWidth: 32, px: 1, textTransform: 'none' }}
  >
    👁 View
  </Button>
  <Button
    variant="outlined"
    color="success"
    size="small"
    onClick={() => handleEdit(row)}
    sx={{ borderRadius: 2, minWidth: 32, px: 1, textTransform: 'none' }}
  >
    ✏️ Edit
  </Button>
  <Button
    variant="outlined"
    color="error"
    size="small"
    onClick={() => handleDelete(row)}
    sx={{ borderRadius: 2, minWidth: 32, px: 1, textTransform: 'none' }}
  >
    🗑 Delete
  </Button>
</Stack>



      <TableContainer
  component={Paper}
  sx={{
    boxShadow: 3,
    borderRadius: 2,
    overflow: 'hidden',
  }}
>
  <Table>
    <TableHead sx={{ backgroundColor: 'primary.main' }}>
      <TableRow>
        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Name</TableCell>
        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Phone</TableCell>
        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Actions</TableCell>
      </TableRow>
    </TableHead>
    <TableBody>
      {customers.map((row, index) => (
        <TableRow
          key={row.id}
          sx={{
            backgroundColor: index % 2 === 0 ? 'grey.100' : 'background.paper',
            '&:hover': { backgroundColor: 'grey.200' },
          }}
        >
          <TableCell>{row.name}</TableCell>
          <TableCell>{row.phone}</TableCell>
          <TableCell>
            {/* Actions Here */}
          </TableCell>
        </TableRow>
      ))}
    </TableBody>
  </Table>
</TableContainer>


      {totalPages > 1 && (
        <Box mt={3} display="flex" justifyContent="center">
          <Pagination
            count={totalPages}
            page={page}
            onChange={(e, value) => setPage(value)}
            color="primary"
          />
        </Box>
      )}
    </Box>
  );
};

export default CustomerList;

 
 
 
