import React from "react";
import CustomerDetail from "./CustomerDetail";

const sampleItem = {
  name: "Mohamed",
  phone: "0750-000-0000",
  products: [
    { name: "TV", price: 300 },
    { name: "Fridge", price: 500 },
  ],
};

const App = () => {
  return (
    <div>
      <CustomerDetail
        item={sampleItem}
        onEdit={(item) => console.log("Edit customer", item)}
        onDelete={(item) => console.log("Delete customer", item)}
        onEditProduct={(product) => console.log("Edit product", product)}
        onDeleteProduct={(product) => console.log("Delete product", product)}
      />
    </div>
  );
};

export default App;




