import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../api';
import './CustomerForm.css';

const CustomerForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });

  useEffect(() => {
    if (id && id !== 'new') {
      api.get('/customers').then(res => {
        const existing = res.data.find(c => c._id === id);
        if (existing) setForm(existing);
      });
    }
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!id || id === 'new') {
        await api.post('/customers', form);
      } else {
        await api.put(`/customers/${id}`, form);
      }
      navigate('/');
    } catch (err) {
      alert('Error saving customer');
      console.error(err);
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="customer-form" style={{ direction: 'rtl' }}>
        <h1>{!id || id === 'new' ? 'Add Customer' : 'Edit Customer'}</h1>

        <div className="row">
          <input name="name" placeholder="ŸÜÿßŸÅ" value={form.name} onChange={handleChange} required />
          <input name="email" placeholder="ÿ¶€åŸÖ€ï€å⁄µ" value={form.email} onChange={handleChange} />
          <input name="phone" placeholder="ÿ™€ïŸÑ€ïŸÅ€ÜŸÜ" value={form.phone} onChange={handleChange} />
        </div>

        <div className="row">
          <input name="address" placeholder="ŸÜÿßŸÅŸàŸÜ€åÿ¥ÿßŸÜ" value={form.address} onChange={handleChange} />
        </div>

        <div className="button-row">
          <button type="button" className="back-button" onClick={() => navigate(-1)}>ÿ≤ŸÅÿ±€åŸÜ üîô</button>
          <button type="submit" className="save-button">üíæ Save</button>
        </div>
      </form>
    </div>
  );
};

export default CustomerForm;
