import React from 'react';
import {
  Card,
  CardContent,
  Typography,
  Grid,
  Avatar,
  Divider,
  Box,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import { styled } from '@mui/material/styles';

const StyledCard = styled(Card)(({ theme }) => ({
  maxWidth: 1200,
  margin: '2rem auto',
  borderRadius: '20px',
  boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
  padding: '1rem',
}));

const SectionTitle = styled(Typography)(({ theme }) => ({
  fontWeight: 'bold',
  marginTop: theme.spacing(2),
  marginBottom: theme.spacing(1),
  fontSize: '1.2rem',
  color: theme.palette.primary.main,
}));

const StyledAvatar = styled(Avatar)(({ theme }) => ({
  width: theme.spacing(12),
  height: theme.spacing(12),
  marginBottom: theme.spacing(1),
}));

const CustomerDetail = ({ customer }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  if (!customer) return <Typography>Loading...</Typography>;

  return (
    <StyledCard>
      <Grid container spacing={4}>
        {/* Customer Info Section */}
        <Grid item xs={12} md={4} textAlign="center">
          <Box display="flex" flexDirection="column" alignItems="center">
            <StyledAvatar src={customer.image} alt={customer.name} />
            <Typography variant="h6">{customer.name}</Typography>
            <Typography color="text.secondary">{customer.email}</Typography>
            <Typography color="text.secondary">{customer.phone}</Typography>
          </Box>
        </Grid>

        {/* Details Section */}
        <Grid item xs={12} md={8}>
          <SectionTitle>Customer Info</SectionTitle>
          <Grid container spacing={2}>
            <Grid item xs={6}>
              <Typography variant="body2" color="text.secondary">Age</Typography>
              <Typography>{customer.age}</Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="body2" color="text.secondary">Gender</Typography>
              <Typography>{customer.gender}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography variant="body2" color="text.secondary">Address</Typography>
              <Typography>{customer.address}</Typography>
            </Grid>
          </Grid>

          <Divider sx={{ my: 2 }} />

          <SectionTitle>Products</SectionTitle>
          <Grid container spacing={2}>
            {customer.products?.length > 0 ? (
              customer.products.map((product, index) => (
                <Grid item xs={12} sm={6} md={4} key={index}>
                  <Card sx={{ height: '100%', borderRadius: '15px' }}>
                    <CardContent>
                      <Typography variant="subtitle1" fontWeight="bold">
                        {product.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Price: ${product.price}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))
            ) : (
              <Grid item xs={12}>
                <Typography>No products available.</Typography>
              </Grid>
            )}
          </Grid>
        </Grid>
      </Grid>
    </StyledCard>
  );
};

export default CustomerDetail;




