import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box, Button, Grid, Paper, TextField, Typography,
  InputLabel, Input, FormControl
} from '@mui/material';
import api from '../api';

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('❌ Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        maxWidth: 600,
        margin: 'auto',
        padding: 4,
        backgroundColor: 'background.paper',
        boxShadow: 3,
        borderRadius: 2
      }}
    >
      <Typography variant="h5" gutterBottom>
        {!itemId || itemId === 'new' ? 'Add Product' : 'Edit Product'}
      </Typography>

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            name="name"
            label="Name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            fullWidth
            name="description"
            label="Description"
            value={form.description}
            onChange={handleChange}
            multiline
            rows={3}
          />
        </Grid>

        <Grid item xs={6}>
          <TextField
            fullWidth
            name="quantity"
            label="Quantity"
            type="number"
            inputProps={{ min: 1 }}
            value={form.quantity}
            onChange={handleChange}
            required
          />
        </Grid>

        <Grid item xs={6}>
          <TextField
            fullWidth
            name="unitPrice"
            label="Unit Price"
            type="number"
            inputProps={{ min: 0, step: 0.01 }}
            value={form.unitPrice}
            onChange={handleChange}
            required
          />
        </Grid>

        <Grid item xs={12}>
          <FormControl fullWidth>
            <InputLabel htmlFor="file">Upload Image</InputLabel>
            <Input
              type="file"
              inputProps={{ accept: 'image/*' }}
              onChange={handleFileChange}
            />
          </FormControl>
        </Grid>

        {preview && (
          <Grid item xs={12}>
            <Box
              component="img"
              src={preview}
              alt="Preview"
              sx={{
                width: 120,
                height: 120,
                objectFit: 'cover',
                borderRadius: 2,
                border: '1px solid #ccc'
              }}
            />
          </Grid>
        )}

        <Grid item xs={12} display="flex" justifyContent="flex-end">
          <Button type="submit" variant="contained" color="primary">
            💾 Save Product
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ItemForm;




