import React from "react";
import {
  Card,
  CardContent,
  Typography,
  Grid,
  IconButton,
  Divider,
  Button,
} from "@mui/material";
import { Edit, Delete } from "@mui/icons-material";

const CustomerDetail = ({ item, onEdit, onDelete, onEditProduct, onDeleteProduct }) => {
  if (!item) {
    return <Typography variant="h6">Loading...</Typography>;
  }

  return (
    <Card sx={{ p: 2, mt: 2 }}>
      <CardContent>
        <Typography variant="h4" gutterBottom>
          {item.name}
        </Typography>
        <Typography variant="subtitle1" gutterBottom>
          Phone: {item.phone}
        </Typography>

        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item>
            <Button
              variant="contained"
              color="primary"
              startIcon={<Edit />}
              onClick={() => onEdit(item)}
            >
              Edit Customer
            </Button>
          </Grid>
          <Grid item>
            <Button
              variant="outlined"
              color="error"
              startIcon={<Delete />}
              onClick={() => onDelete(item)}
            >
              Delete Customer
            </Button>
          </Grid>
        </Grid>

        <Divider sx={{ my: 3 }} />

        <Typography variant="h5" gutterBottom>
          Products
        </Typography>
        {item.products && item.products.length > 0 ? (
          item.products.map((product, index) => (
            <Card key={index} sx={{ my: 1, p: 2 }}>
              <Grid container justifyContent="space-between" alignItems="center">
                <Grid item xs={12} sm={8}>
                  <Typography variant="subtitle1">
                    {product.name} - {product.price}$
                  </Typography>
                </Grid>
                <Grid item xs={12} sm={4} textAlign="right">
                  <IconButton onClick={() => onEditProduct(product)}>
                    <Edit />
                  </IconButton>
                  <IconButton onClick={() => onDeleteProduct(product)}>
                    <Delete />
                  </IconButton>
                </Grid>
              </Grid>
            </Card>
          ))
        ) : (
          <Typography variant="body2">No products available.</Typography>
        )}
      </CardContent>
    </Card>
  );
};

export default CustomerDetail;



