import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';



const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('❌ Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <button
          className="btn btn-secondary"
          onClick={() => navigate(`/customer/${customerId}`)}
        >
          🔙 Back
        </button>
        <h2>{!itemId || itemId === 'new' ? '➕ Add Product' : '✏️ Edit Product'}</h2>
        
      </div>

      <form dir="rtl" onSubmit={handleSubmit} className="bg-light p-4 rounded shadow-sm">

        <div className="row mb-3">
          <div className="col-md-6 mb-3">
            <label className="form-label">Name</label>
            <input
              name="name"
              className="form-control"
              placeholder="Product Name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="col-md-6 mb-3">
            <label className="form-label">Description</label>
            <input
              name="description"
              className="form-control"
              placeholder="Short Description"
              value={form.description}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="row mb-3">
          <div className="col-md-6 mb-3">
            <label className="form-label">Quantity</label>
            <input
              name="quantity"
              type="number"
              min="1"
              className="form-control"
              value={form.quantity}
              onChange={handleChange}
              required
            />
          </div>
          <div className="col-md-6 mb-3">
            <label className="form-label">Unit Price</label>
            <input
              name="unitPrice"
              type="number"
              step="0.01"
              min="0"
              className="form-control"
              value={form.unitPrice}
              onChange={handleChange}
              required
            />
          </div>
        </div>


        {preview && (
  <div className="mb-3">
    <label className="form-label">Preview</label>
    <div>
      <img src={preview} alt="Preview" style={{ maxWidth: '200px', borderRadius: '10px' }} />
    </div>
  </div>
)}


        <div className="d-flex justify-content-end">
          <button type="submit" className="btn btn-success">
            💾 Save Product
          </button>
        </div>
      </form>
    </div>
  );
};

export default ItemForm;
