import React, { useEffect, useState } from 'react';
import {
  Box,
  Grid,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  CardMedia,
  useTheme,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();
  const theme = useTheme();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value,
    });
  };

  const handleFileChange = e => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => formData.append(key, value));
    if (image) formData.append('image', image);
    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('Error saving item:', err);
      alert('Error: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: theme.palette.mode === 'dark'
          ? 'linear-gradient(to right, #0f2027, #203a43, #2c5364)'
          : 'linear-gradient(to right, #fdfbfb, #ebedee)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 3,
        direction: 'rtl',
      }}
    >
      <Card sx={{ maxWidth: 700, width: '100%', p: 2, borderRadius: 4, boxShadow: 6 }}>
        <CardContent>
          <Typography variant="h5" gutterBottom textAlign="center" fontWeight="bold">
            {itemId === 'new' ? '‚ûï ÿ≤€åÿßÿØ⁄©ÿ±ÿØŸÜ€å ⁄©ÿß⁄µÿß' : '‚úèÔ∏è ⁄Ø€Ü⁄ï€åŸÜ€å ÿ≤ÿßŸÜ€åÿßÿ±€å ⁄©ÿß⁄µÿß'}
          </Typography>

          <Box component="form" onSubmit={handleSubmit} mt={3}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  name="name"
                  label="ŸÜÿßŸà€å ⁄©ÿß⁄µÿß"
                  value={form.name}
                  onChange={handleChange}
                  fullWidth
                  required
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  name="description"
                  label="Ÿà€ïÿ≥ŸÅ€å ⁄©ÿß⁄µÿß"
                  value={form.description}
                  onChange={handleChange}
                  fullWidth
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  name="quantity"
                  label="⁄òŸÖÿßÿ±€ï"
                  type="number"
                  inputProps={{ min: 1 }}
                  value={form.quantity}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>

              <Grid item xs={12} sm={6}>
                <TextField
                  name="unitPrice"
                  label="ŸÜÿ±ÿÆ€å €å€ï⁄©€ï"
                  type="number"
                  inputProps={{ min: 0, step: 0.01 }}
                  value={form.unitPrice}
                  onChange={handleChange}
                  fullWidth
                  required
                />
              </Grid>

              {/* <Grid item xs={12}>
                <Button variant="contained" component="label" fullWidth>
                  ÿ®ÿßÿ±⁄©ÿ±ÿØŸÜ€å Ÿà€éŸÜ€ï
                  <input type="file" hidden onChange={handleFileChange} />
                </Button>
              </Grid> */}

              {preview && (
                <Grid item xs={12}>
                  <Typography variant="subtitle2">Ÿà€éŸÜ€ï€å Ÿæ€éÿ¥ÿ®€åŸÜ€åŸÜ:</Typography>
                  <CardMedia
                    component="img"
                    src={preview}
                    alt="Preview"
                    sx={{
                      maxHeight: 200,
                      objectFit: 'contain',
                      borderRadius: 2,
                      mt: 1,
                      boxShadow: 3,
                    }}
                  />
                </Grid>
              )}
            </Grid>

            <Box mt={4} display="flex" justifyContent="space-between">
              <Button
                variant="outlined"
                color="perimary"
                onClick={() => navigate(`/customer/${customerId}`)}
              >
                ‚¨ÖÔ∏è ÿ≤ŸÅÿ±€åŸÜ
              </Button>

              <Button type="submit" variant="contained" color="primary">
                üíæ Ÿæÿßÿ¥€ï⁄©€ïŸàÿ™⁄©ÿ±ÿØŸÜ
              </Button>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ItemForm;





