import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';

const CustomerReceipt = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [items, setItems] = useState([]);
  const [billNo, setBillNo] = useState(null);

  useEffect(() => {
    const load = async () => {
      try {
        const [customerRes, itemRes] = await Promise.all([
          api.get('/customers'),
          api.get('/items')
        ]);

        const sortedCustomers = customerRes.data.sort((a, b) =>
          a.name.localeCompare(b.name, 'ku', { sensitivity: 'base' })
        );
        const foundIndex = sortedCustomers.findIndex(c => c._id === id);
        const foundCustomer = sortedCustomers[foundIndex];
        const customerItems = itemRes.data.items.filter(
          i => i.customerId === id || i.customerId?._id === id
        );

        setCustomer(foundCustomer);
        setItems(customerItems);
        setBillNo(1000 + foundIndex + 1);
      } catch (err) {
        console.error('Failed to load receipt:', err);
      }
    };

    load();
  }, [id]);

  if (!customer) return <p>Loading...</p>;

  const total = items.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
  const paid = customer.payments?.reduce((sum, p) => sum + p.amount, 0) || 0;
  const remaining = total - paid;

  return (
    <div className="receipt-template">
      {/* Header */}
      <div className="receipt-header">
        <div className="logo-box">LOGO</div>
        <h1>ناوەندێ بازرگانی یەکگرتوو</h1>
        <p>*********** - *********** - 0750 XXX XXXX</p>
      </div>

      {/* Customer Info */}
      <div className="receipt-meta">
        <div><strong>ژ.مارە (Bill No):</strong> {billNo}</div>
        <div><strong>ناوی کرێدار:</strong> {customer.name}</div>
        <div><strong>مۆبایل:</strong> {customer.phone}</div>
      </div>

      {/* Table */}
      <table className="receipt-table">
        <thead>
          <tr>
            <th>گەشتی</th>
            <th>نرخی یەکە</th>
            <th>ژمارە</th>
            <th>ناوی كالا</th>
            <th>ڕیز</th>



            {/* <th>ڕیز</th>
            <th>ناوی كالا</th>
            <th>ژمارە</th>
            <th>نرخی یەکە</th>
            <th>گەشتی</th> */}
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: 20 }, (_, index) => {
            const item = items[index];
            return (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item?.name || ''}</td>
                <td>{item?.quantity || ''}</td>
                <td>{item?.unitPrice ? `$${item.unitPrice}` : ''}</td>
                <td>{item?.totalPrice ? `$${item.totalPrice.toFixed(2)}` : ''}</td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Summary */}
      <div className="receipt-summary">
        <div><strong>💰 گشتی:</strong> ${total.toFixed(2)}</div>
        <div><strong>💵 پارەی داوە:</strong> ${paid.toFixed(2)}</div>
        <div><strong>📌 ماوە:</strong> ${remaining.toFixed(2)}</div>
      </div>

      <div style={{ marginTop: '2rem', textAlign: 'center' }}>
        <button onClick={() => window.print()} className="button print-only">
          🖨️ Print
        </button>
      </div>
    </div>
  );
};

export default CustomerReceipt;
