import React from 'react';
import {
  Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Button, Stack
} from '@mui/material';
import './Name.js'

const CustomerTable = ({ customers, onView, onEdit, onDelete }) => {
  return (
    <TableContainer
      component={Paper}
      sx={{
        boxShadow: 3,
        borderRadius: 2,
        overflow: 'hidden',
        mt: 2
      }}
    >
      <Table>
        <TableHead sx={{ backgroundColor: 'primary.main' }}>
          <TableRow>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>üë§ Name</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>üìû Phone</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }} align="center">‚öôÔ∏è Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {customers.map((row, index) => (
            <TableRow
              key={row.id}
              sx={{
                backgroundColor: index % 2 === 0 ? 'grey.100' : 'background.paper',
                '&:hover': { backgroundColor: 'grey.200' }
              }}
            >
              <TableCell>{row.name}</TableCell>
              <TableCell>{row.phone}</TableCell>
              <TableCell align="center">
                <Stack direction="row" spacing={1} justifyContent="center">
                  <Button
                    variant="outlined"
                    color="primary"
                    size="small"
                    onClick={() => onView(row)}
                    sx={{ borderRadius: 2, textTransform: 'none', px: 1.5 }}
                  >
                    üëÅ View
                  </Button>
                  <Button
                    variant="outlined"
                    color="success"
                    size="small"
                    onClick={() => onEdit(row)}
                    sx={{ borderRadius: 2, textTransform: 'none', px: 1.5 }}
                  >
                    ‚úèÔ∏è Edit
                  </Button>
                  <Button
                    variant="outlined"
                    color="error"
                    size="small"
                    onClick={() => onDelete(row)}
                    sx={{ borderRadius: 2, textTransform: 'none', px: 1.5 }}
                  >
                    üóë Delete
                  </Button>
                </Stack>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default CustomerTable;


 
 
 
