import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import {
  Box, Button, Container, Grid, TextField, Typography, Card, CardMedia, IconButton
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import SaveIcon from '@mui/icons-material/Save';

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then(res => {
        const item = res.data.items.find(i => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('❌ Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Container maxWidth="sm">
      <Box sx={{ my: 4, p: 3, boxShadow: 3, borderRadius: 3, backgroundColor: '#f9f9f9' }}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
          <Typography variant="h5">
            {itemId === 'new' ? 'Add Product' : 'Edit Product'}
          </Typography>
          <IconButton color="primary" onClick={() => navigate(`/customer/${customerId}`)}>
            <ArrowBackIcon />
          </IconButton>
        </Box>

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                label="Product Name"
                name="name"
                value={form.name}
                onChange={handleChange}
                fullWidth
                required
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                label="Description"
                name="description"
                value={form.description}
                onChange={handleChange}
                fullWidth
                multiline
                rows={3}
              />
            </Grid>

            <Grid item xs={6}>
              <TextField
                label="Quantity"
                name="quantity"
                type="number"
                value={form.quantity}
                onChange={handleChange}
                fullWidth
                required
                inputProps={{ min: 1 }}
              />
            </Grid>

            <Grid item xs={6}>
              <TextField
                label="Unit Price"
                name="unitPrice"
                type="number"
                value={form.unitPrice}
                onChange={handleChange}
                fullWidth
                required
                inputProps={{ step: '0.01', min: 0 }}
              />
            </Grid>

            <Grid item xs={12}>
              <Button variant="outlined" component="label" fullWidth>
                Upload Image
                <input type="file" hidden accept="image/*" onChange={handleFileChange} />
              </Button>
            </Grid>

            {preview && (
              <Grid item xs={12}>
                <Card sx={{ maxWidth: 200, mx: 'auto', borderRadius: 2 }}>
                  <CardMedia
                    component="img"
                    height="160"
                    image={preview}
                    alt="Preview"
                    sx={{ objectFit: 'cover' }}
                  />
                </Card>
              </Grid>
            )}

            <Grid item xs={12}>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                startIcon={<SaveIcon />}
                fullWidth
              >
                Save Product
              </Button>
            </Grid>
          </Grid>
        </form>
      </Box>
    </Container>
  );
};

export default ItemForm;



