import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import {
  Box,
  Button,
  Stack,
  TextField,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Pagination,
} from '@mui/material';
import api from '../api';
import './CustomerList.css'; // 🔄 Import CSS

const ITEMS_PER_PAGE = 5;

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [selectedLetter, setSelectedLetter] = useState('');

  useEffect(() => {
    api.get('/customers').then((res) => {
      const customersWithBill = res.data.map((c, i) => ({
        ...c,
        billNo: 1000 + i + 1,
      }));
      setCustomers(customersWithBill);
    });
  }, []);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
    setPage(1);
  };

  const handleLetterClick = (letter) => {
    setSelectedLetter(letter);
    setPage(1);
  };

  const filteredCustomers = customers.filter((c) => {
    const lowerSearch = search.toLowerCase();
    const matchesSearch =
      c.name.toLowerCase().includes(lowerSearch) ||
      c.phone?.toLowerCase().includes(lowerSearch) ||
      c.email?.toLowerCase().includes(lowerSearch) ||
      c.address?.toLowerCase().includes(lowerSearch) ||
      c.billNo?.toString().includes(lowerSearch);

    const matchesLetter = selectedLetter === '' || c.name.startsWith(selectedLetter);
    return matchesSearch && matchesLetter;
  });

  const totalPages = Math.ceil(filteredCustomers.length / ITEMS_PER_PAGE);
  const visibleCustomers = filteredCustomers.slice(
    (page - 1) * ITEMS_PER_PAGE,
    page * ITEMS_PER_PAGE
  );

  const letters = Array.from(
    new Set(customers.map((c) => c.name[0]?.toUpperCase()).sort((a, b) => a.localeCompare(b)))
  );

  return (
    <Box p={2}>
      <Typography variant="h4" align="center" gutterBottom>
        📋 Customer List
      </Typography>

      <Stack
        direction={{ xs: 'column', sm: 'row' }}
        spacing={2}
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
        mb={3}
      >
        <TextField
          variant="outlined"
          size="small"
          placeholder="🔍 Search by name, phone, email, address, or bill..."
          value={search}
          onChange={handleSearchChange}
          sx={{ minWidth: 250 }}
        />
        <Button component={Link} to="/customer/new" variant="contained" color="primary">
          ➕ Add Customer
        </Button>
      </Stack>

      <Stack
        direction="row"
        spacing={1}
        justifyContent="center"
        flexWrap="wrap"
        mb={2}
      >
        <Button
          variant={selectedLetter === '' ? 'contained' : 'outlined'}
          onClick={() => setSelectedLetter('')}
        >
          🌐 All
        </Button>
        {letters.map((letter) => (
          <Button
            key={letter}
            variant={selectedLetter === letter ? 'contained' : 'outlined'}
            onClick={() => handleLetterClick(letter)}
          >
            {letter}
          </Button>
        ))}
      </Stack>

      <TableContainer component={Paper} className="table-container">
        <Table className="customer-table">
          <TableHead>
            <TableRow>
              <TableCell>Bill No</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Address</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {visibleCustomers.length === 0 ? (
              <TableRow>
                <TableCell colSpan={6} align="center">
                  No customers found.
                </TableCell>
              </TableRow>
            ) : (
              visibleCustomers.map((c) => (
                <TableRow key={c._id}>
                  <TableCell>{c.billNo}</TableCell>
                  <TableCell>{c.name}</TableCell>
                  <TableCell>{c.phone}</TableCell>
                  <TableCell>{c.email}</TableCell>
                  <TableCell>{c.address}</TableCell>
                  <TableCell>
                    <Button
                      component={Link}
                      to={`/customer/${c._id}`}
                      size="small"
                      variant="outlined"
                    >
                      View
                    </Button>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>

      {totalPages > 1 && (
        <Box mt={3} display="flex" justifyContent="center">
          <Pagination
            count={totalPages}
            page={page}
            onChange={(e, value) => setPage(value)}
            color="primary"
          />
        </Box>
      )}
    </Box>
  );
};

export default CustomerList;

 
 
 
