import React, { useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField
} from '@mui/material';
import './CustomerDetail.css';

const CustomerDetail = ({ item, onEdit, onDelete, onEditProduct, onDeleteProduct }) => {
  const [editProduct, setEditProduct] = useState(null);
  const [editName, setEditName] = useState('');
  const [editPrice, setEditPrice] = useState('');

  const handleEditClick = (product) => {
    setEditProduct(product);
    setEditName(product.name);
    setEditPrice(product.price);
  };

  const handleSave = () => {
    onEditProduct(editProduct.id, { name: editName, price: editPrice });
    setEditProduct(null);
  };

  return (
    <Container className="item-detail">
      <Typography variant="h4" gutterBottom>
        {item.name}
      </Typography>
      <Typography variant="body1">Phone: {item.phone}</Typography>
      <Typography variant="body1">Location: {item.location}</Typography>
      <Typography variant="body1">Note: {item.note}</Typography>
      <Typography variant="body1">Date: {item.date}</Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 2, mr: 2 }}
        onClick={() => onEdit(item.id)}
      >
        Edit
      </Button>
      <Button
        variant="outlined"
        color="error"
        sx={{ mt: 2 }}
        onClick={() => onDelete(item.id)}
      >
        Delete
      </Button>

      <Table sx={{ mt: 4 }}>
        <TableHead>
          <TableRow>
            <TableCell><strong>Product Name</strong></TableCell>
            <TableCell><strong>Price</strong></TableCell>
            <TableCell><strong>Actions</strong></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {item.products?.map((product) => (
            <TableRow key={product.id}>
              <TableCell>{product.name}</TableCell>
              <TableCell>${product.price}</TableCell>
              <TableCell>
                <Button onClick={() => handleEditClick(product)}>Edit</Button>
                <Button color="error" onClick={() => onDeleteProduct(product.id)}>Delete</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Dialog open={!!editProduct} onClose={() => setEditProduct(null)}>
        <DialogTitle>Edit Product</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Product Name"
            value={editName}
            onChange={(e) => setEditName(e.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Price"
            type="number"
            value={editPrice}
            onChange={(e) => setEditPrice(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditProduct(null)}>Cancel</Button>
          <Button onClick={handleSave} variant="contained" color="primary">Save</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default CustomerDetail;



