import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Grid,
  TextField,
  Button,
  Typography,
  Avatar,
  Input,
  Paper,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import api from '../api';

// üí´ Custom Gradient Button
const GradientButton = styled(Button)({
  background: 'linear-gradient(to right, #6a11cb, #2575fc)',
  color: '#fff',
  padding: '10px 20px',
  borderRadius: '12px',
  fontWeight: 'bold',
  boxShadow: '0 4px 20px rgba(0,0,0,0.2)',
  transition: '0.3s',
  '&:hover': {
    background: 'linear-gradient(to right, #5e10b2, #1e63d9)',
    transform: 'scale(1.03)',
  },
});

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then((res) => {
        const item = res.data.items.find((i) => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Paper
      elevation={6}
      sx={{
        maxWidth: 600,
        mx: 'auto',
        mt: 5,
        p: 4,
        borderRadius: 5,
        background: 'rgba(255, 255, 255, 0.9)',
        backdropFilter: 'blur(8px)',
      }}
    >
      <Typography variant="h4" fontWeight="bold" gutterBottom textAlign="center">
        {itemId && itemId !== 'new' ? '‚úèÔ∏è Edit Product' : '‚ûï Add Product'}
      </Typography>

      <Box component="form" onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Product Name"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Description"
              name="description"
              multiline
              rows={3}
              value={form.description}
              onChange={handleChange}
              variant="outlined"
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              fullWidth
              type="number"
              label="Quantity"
              name="quantity"
              value={form.quantity}
              onChange={handleChange}
              inputProps={{ min: 1 }}
              variant="outlined"
              required
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              fullWidth
              type="number"
              label="Unit Price ($)"
              name="unitPrice"
              value={form.unitPrice}
              onChange={handleChange}
              inputProps={{ min: 0, step: 0.01 }}
              variant="outlined"
              required
            />
          </Grid>

          <Grid item xs={12}>
            <Button variant="outlined" component="label" fullWidth sx={{ borderRadius: 2 }}>
              Upload Image
              <Input type="file" hidden accept="image/*" onChange={handleFileChange} />
            </Button>
          </Grid>

          {preview && (
            <Grid item xs={12} textAlign="center">
              <Avatar
                alt="Preview"
                src={preview}
                sx={{
                  width: 100,
                  height: 100,
                  margin: 'auto',
                  boxShadow: 2,
                  border: '3px solid #fff',
                }}
              />
            </Grid>
          )}

          <Grid item xs={12} textAlign="center">
            <GradientButton type="submit">
              üíæ Save Product
            </GradientButton>
          </Grid>
        </Grid>
      </Box>
    </Paper>
  );
};

export default ItemForm;



