import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import {
  Box, Typography, Button, Card, CardContent, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, Paper, Dialog, DialogTitle, DialogContent,
  DialogActions, TextField, Chip, Stack
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import EditIcon from '@mui/icons-material/Edit';
import ReceiptIcon from '@mui/icons-material/Receipt';
import AddIcon from '@mui/icons-material/Add';
import EmailIcon from '@mui/icons-material/Email';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';


import api from '../api';
import './CustomerDetail.css';

const CustomerDetail = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [items, setItems] = useState([]);
  const [showPayments, setShowPayments] = useState(false);
  const [paymentAmount, setPaymentAmount] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const customerRes = await api.get('/customers');
        const sorted = customerRes.data.sort((a, b) =>
          a.name.localeCompare(b.name, 'ku', { sensitivity: 'base' })
        );
        const index = sorted.findIndex(c => c._id === id);
        const found = sorted[index];
        if (found) {
          found.billNo = 1000 + index + 1;
          setCustomer(found);
        }

        const itemsRes = await api.get('/items');
        const filtered = itemsRes.data.items.filter(i =>
          i.customerId === id || i.customerId?._id === id
        );
        setItems(filtered);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [id]);

  const handleDeleteItem = async (itemId) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      await api.delete(`/items/${itemId}`);
      window.location.reload();
    }
  };

  const registerPayment = async () => {
    if (remaining <= 0) return alert('Already paid in full.');
    try {
      await api.post(`/customers/${customer._id}/payments`, {
        amount: parseFloat(paymentAmount)
      });
      setShowPayments(false);
      setPaymentAmount('');
      window.location.reload();
    } catch (err) {
      alert('Payment failed');
      console.error(err);
    }
  };

  if (!customer) return <Typography>Loading...</Typography>;

  const totalValue = items.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
  const totalPaid = customer.payments?.reduce((sum, p) => sum + p.amount, 0) || 0;
  const remaining = totalValue - totalPaid;

  const whatsappLink = `https://wa.me/${customer.phone?.replace(/\D/g, '')}?text=Hello%20${encodeURIComponent(customer.name)}`;
  const mailLink = `mailto:${customer.email}?subject=Inventory Update`;

  return (
    <Box className="item-detail">
      <Button style={{ margin: 'left' }} component={Link} to="/" startIcon={<ArrowBackIcon />} variant="outlined">
        Back
      </Button>

      <Card sx={{ mt: 3, mb: 2 }}>
        <CardContent>
          <Typography variant="h4">{customer.name}</Typography>
          <Typography>üßæ Bill No: {customer.billNo}</Typography>
          <Typography>üìç {customer.address || 'No address'}</Typography>
          <Typography>üìû {customer.phone || 'No phone'}</Typography>
          <Typography>‚úâÔ∏è {customer.email || 'No email'}</Typography>

          <Stack direction="row" spacing={1} mt={2} flexWrap="wrap">
            {customer.phone && (
              <Button href={whatsappLink} target="_blank" variant="contained" color="success" startIcon={<WhatsAppIcon />}>
                WhatsApp
              </Button>
            )}
            {customer.email && (
              <Button href={mailLink} variant="contained" color="info" startIcon={<EmailIcon />}>
                Email
              </Button>
            )}
            <Button component={Link} to={`/customer/${customer._id}/edit`} startIcon={<EditIcon />} variant="contained">
              Edit
            </Button>
            <Button component={Link} to={`/customer/${customer._id}/receipt`} startIcon={<ReceiptIcon />} variant="contained" color="secondary">
              Receipt
            </Button>
            {remaining > 0 ? (
              <Button variant="contained" color="warning" onClick={() => setShowPayments(true)}>
                üí∏ Register Payment
              </Button>
            ) : (
              <Chip icon={<CheckCircleIcon />} label="Paid in Full" color="success" />
            )}
          </Stack>
        </CardContent>
      </Card>

      <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
        <Typography variant="h5">üì¶ Products</Typography>
        <Button component={Link} to={`/customer/${customer._id}/item/new`} startIcon={<AddIcon />} variant="outlined">
          Add Product
        </Button>
      </Box>

      {items.length === 0 ? (
        <Typography>No items for this customer.</Typography>
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Image</TableCell>
                <TableCell>Product</TableCell>
                <TableCell>Qty</TableCell>
                <TableCell>Unit Price</TableCell>
                <TableCell>Total</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {items.map(item => (
                <TableRow key={item._id}>
                  <TableCell>
                    {item.imageUrl ? (
                      <img src={`http://localhost:5000${item.imageUrl}`} alt={item.name} style={{ width: 60, height: 60, borderRadius: 8 }} />
                    ) : '‚Äî'}
                  </TableCell>
                  <TableCell>{item.name}</TableCell>
                  <TableCell>{item.quantity}</TableCell>
                  <TableCell>${item.unitPrice}</TableCell>
                  <TableCell>${item.totalPrice.toFixed(2)}</TableCell>
                  <TableCell>
                    <Stack direction="row" spacing={1}>
                      <Button component={Link} to={`/customer/${customer._id}/item/${item._id}/edit`} variant="outlined">
                        ‚úèÔ∏è
                      </Button>
                      <Button onClick={() => handleDeleteItem(item._id)} variant="contained" color="error">
                        üóë
                      </Button>
                    </Stack>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      <Box mt={2} textAlign="right">
        <Typography variant="h6">üßæ Total: ${totalValue.toFixed(2)}</Typography>
        <Typography>üí≥ Paid: ${totalPaid.toFixed(2)}</Typography>
        <Typography>üí∞ Remaining: ${remaining.toFixed(2)}</Typography>
        {customer.payments?.length > 0 && (
          <details>
            <summary>üìÜ Payment History</summary>
            <ul>
              {customer.payments.map((p, i) => (
                <li key={i}>üíµ ${p.amount} on {new Date(p.date).toLocaleDateString()}</li>
              ))}
            </ul>
          </details>
        )}
      </Box>

      <Dialog open={showPayments} onClose={() => setShowPayments(false)}>
        <DialogTitle>Register Payment</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Amount"
            type="number"
            fullWidth
            value={paymentAmount}
            onChange={(e) => setPaymentAmount(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={registerPayment}>üíæ Save</Button>
          <Button onClick={() => setShowPayments(false)} color="error">‚ùå Cancel</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default CustomerDetail;



