import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api';
import tablelogo from '../assets/tablelogo.png';
import { Container, Typography, Box, Button, Grid } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import PrintIcon from '@mui/icons-material/Print';
import '../pages/CustomerReceipt.css';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';


const CustomerReceipt = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [items, setItems] = useState([]);
  const [billNo, setBillNo] = useState(null);
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const load = async () => {
      try {
        const [customerRes, itemRes] = await Promise.all([
          api.get('/customers'),
          api.get('/items')
        ]);

        const sortedCustomers = customerRes.data.sort((a, b) =>
          a.name.localeCompare(b.name, 'ku', { sensitivity: 'base' })
        );
        const foundIndex = sortedCustomers.findIndex(c => c._id === id);
        const foundCustomer = sortedCustomers[foundIndex];
        const customerItems = itemRes.data.items.filter(
          i => i.customerId === id || i.customerId?._id === id
        );

        setCustomer(foundCustomer);
        setItems(customerItems);
        setBillNo(1000 + foundIndex + 1);
        setCurrentDateTime(new Date());
      } catch (err) {
        console.error('Failed to load receipt:', err);
      }
    };

    load();
  }, [id]);

  if (!customer) return <Typography>Loading...</Typography>;

  const total = items.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
  const paid = customer.payments?.reduce((sum, p) => sum + p.amount, 0) || 0;
  const remaining = total - paid;

  const formattedDate = currentDateTime.toLocaleDateString();
  const formattedTime = currentDateTime.toLocaleTimeString();

  return (
    <Container maxWidth="md" sx={{   }}>

          <div>
            <Button
              variant="contained"
              startIcon={<ArrowBackIcon />}
              onClick={() => window.history.back()}>
              Back
            </Button>
          </div>


      <br />
      <br />
      {/* Header */}
      <Box textAlign="center" mb={2}>
        <img src={tablelogo} alt="Logo" style={{ width: 200 }} />
        <Typography variant="h5" fontWeight="bold" mt={1} sx={{ fontSize: '21px' }}>
          ŸÜ⁄§€éÿ≥€åŸÜ⁄Ø€ïŸáÿß ÿ¶€ïÿ≠ŸÖÿØ ÿ®ŸàŸà ÿ®ÿßÿ≤ÿ±⁄ØÿßŸÜ€åÿß ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑ€éÿ™ ⁄©ÿßÿ±€ïÿ®€é ÿ® ⁄©ÿ™ Ÿà ⁄©ŸàŸÖ
          <p style={{ fontSize: '20px' }}>Ÿá€ïÿ± Ÿà€ïÿ≥ÿß ÿ¶€ïŸÖ ÿØÿ¶ÿßŸÖÿßÿØ€ï€åŸÜ€ï ÿ®ŸàŸà ÿØÿ±Ÿàÿ≥ÿ™⁄©ÿ±ŸÜÿß Ÿá€ïŸÖ€å ÿ¨Ÿàÿ±€å ÿ¶ÿß⁄§ÿßŸá€åÿß ÿ® ÿØ€åÿ≤ÿß€åŸÜ€éÿ™ ŸÖŸàÿØÿ±ŸÜ Ÿà ÿ≥€ïÿ±ÿØ€ïŸÖÿßŸÜ€ï</p>
        </Typography>
      </Box>

      {/* Date & Bill Info */}
      <Grid item xs={6} textAlign="right">
        <Typography><strong>ÿ®€ïÿ±€éÿ≤:</strong> {customer.name}</Typography>
        <Typography><strong>⁄ò. ŸÖ€Üÿ®ÿß€åŸÑ:</strong> {customer.phone}</Typography>
        <Typography><strong>⁄òŸÖÿßÿ±ÿß ( Ÿæÿ≥ŸàŸÑ€é ) :</strong> {billNo}</Typography>
          <Typography><strong>ŸÖ€é⁄òŸà€åÿß:</strong> {formattedDate}</Typography>
          <Typography sx={{direction: 'rtl'}}><strong>⁄©ÿßÿ™⁄òŸÖ€éÿ±:</strong> {formattedTime}</Typography>
        </Grid>
        
      <Grid container spacing={2} mb={2}>

      <Typography style={{ textAlign: 'center' }}>üìû 07503414123 - üìû 07507325775 - üìû 07504810978</Typography>
      
        <Grid item xs={6}>
          
        </Grid>
        
      </Grid>

      {/* Table */}
            <div className="custom-table-wrapper">
        <table className="custom-table">
          <thead>
            <tr>
              <th>ÿ®Ÿáÿß€å€é ⁄Øÿ¥ÿ™€å</th>
               <th>ÿ®Ÿáÿß€å€é ÿ¶€é⁄©€é</th>
               <th>⁄òŸÖÿßÿ±€ï</th>
               <th>ŸÜÿßŸÅ€é ⁄©€ïŸÑ Ÿà Ÿæ€ïŸÑ€å</th>
               <th>⁄ï€åÿ≤</th>
            </tr>
          </thead>
          <tbody>
            {Array.from({ length: 20 }, (_, index) => {
              const item = items[index];
              return (
                <tr key={index}>
                   <td>{item?.totalPrice ? `$${item.totalPrice.toFixed(2)}` : ''}</td>
                   <td>{item?.unitPrice ? `$${item.unitPrice}` : ''}</td>
                   <td>{item?.quantity || ''}</td>
                   <td>{item?.name || ''}</td>
                   <td>{index + 1}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>


      {/* Summary */}
      <Box mt={3} style={{ borderTop: '1px solid #ccc', paddingTop: '10px', direction: 'rtl' }}>
         <Typography variant="h6"><strong>üíµ Ÿæÿßÿ±€é Ÿáÿßÿ™€å€ï ÿØÿßŸÜ:</strong> ${paid.toFixed(2)}</Typography>
         <Typography variant="h6"><strong>üìå Ÿæÿßÿ±€é ŸÖÿß€å:</strong> ${remaining.toFixed(2)}</Typography>
        <Typography variant="h6"><strong>üí∞ Ÿæÿßÿ±€é ⁄Øÿ¥ÿ™€å:</strong> ${total.toFixed(2)}</Typography>
      </Box>

      <p style={{ textAlign: 'center', marginTop: '20px' }}>ÿÆ€ïŸÑ€ïÿ™ ÿ®ŸàŸÜ ÿ®ŸàŸà Ÿá€ïÿ±ÿØŸàŸà ŸÑÿß ÿØÿ≤ŸÅÿ±€åÿ™</p>

      {/* Buttons */}
      <Box mt={4} display="flex" justifyContent="center" gap={2}>
        {/* <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
