import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../api';
import './CustomerForm.css';

const CustomerForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });

  useEffect(() => {
    if (id && id !== 'new') {
      api.get('/customers').then(res => {
        const existing = res.data.find(c => c._id === id);
        if (existing) setForm(existing);
      });
    }
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    // Ÿæÿß⁄©⁄©ÿ±ÿØŸÜ€å 0 ŸÑ€ï ÿ≥€ïÿ±€ïÿ™ÿß Ÿà ÿ≤€åÿßÿØ⁄©ÿ±ÿØŸÜ€å +964
    let cleanedPhone = form.phone.trim();
    if (cleanedPhone.startsWith('0')) {
      cleanedPhone = cleanedPhone.slice(1);
    }
    const formToSend = {
      ...form,
      phone: `+964${cleanedPhone}`
    };

    if (!id || id === 'new') {
      await api.post('/customers', formToSend);
    } else {
      await api.put(`/customers/${id}`, formToSend);
    }
    navigate('/');
  } catch (err) {
    alert('Error saving customer');
    console.error(err);
  }
};


