import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Grid,
  TextField,
  Button,
  Typography,
  Card,
  CardMedia,
  Avatar,
  Input,
} from '@mui/material';
import api from '../api';

const ItemForm = () => {
  const { customerId, itemId } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    description: '',
    quantity: 1,
    unitPrice: 0,
  });

  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    if (itemId && itemId !== 'new') {
      api.get('/items').then((res) => {
        const item = res.data.items.find((i) => i._id === itemId);
        if (item) {
          setForm({
            name: item.name,
            description: item.description || '',
            quantity: item.quantity || 1,
            unitPrice: item.unitPrice || 0,
          });
          if (item.imageUrl) {
            setPreview(`http://localhost:5000${item.imageUrl}`);
          }
        }
      });
    }
  }, [itemId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: name === 'quantity' || name === 'unitPrice' ? Number(value) : value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) => {
      formData.append(key, value);
    });

    if (image) {
      formData.append('image', image);
    }

    formData.append('customerId', customerId);

    try {
      if (!itemId || itemId === 'new') {
        await api.post('/items', formData);
      } else {
        await api.put(`/items/${itemId}`, formData);
      }
      navigate(`/customer/${customerId}`);
    } catch (err) {
      console.error('‚ùå Error saving item:', err.response?.data || err.message);
      alert('Error saving item: ' + (err.response?.data?.error || err.message));
    }
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        maxWidth: 600,
        mx: 'auto',
        mt: 4,
        p: 3,
        backgroundColor: 'background.paper',
        borderRadius: 3,
        boxShadow: 3,
      }}
    >
      <Typography variant="h5" fontWeight="bold" gutterBottom>
        {!itemId || itemId === 'new' ? '‚ûï Add Product' : '‚úèÔ∏è Edit Product'}
      </Typography>

      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            name="name"
            label="Product Name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            fullWidth
            name="description"
            label="Description"
            value={form.description}
            onChange={handleChange}
            multiline
            rows={3}
          />
        </Grid>

        <Grid item xs={6}>
          <TextField
            fullWidth
            name="quantity"
            type="number"
            label="Quantity"
            value={form.quantity}
            onChange={handleChange}
            inputProps={{ min: 1 }}
            required
          />
        </Grid>

        <Grid item xs={6}>
          <TextField
            fullWidth
            name="unitPrice"
            type="number"
            label="Unit Price ($)"
            value={form.unitPrice}
            onChange={handleChange}
            inputProps={{ min: 0, step: 0.01 }}
            required
          />
        </Grid>

        <Grid item xs={12}>
          <Button variant="outlined" component="label" fullWidth>
            Upload Image
            <Input type="file" hidden accept="image/*" onChange={handleFileChange} />
          </Button>
        </Grid>

        {preview && (
          <Grid item xs={12}>
            <Card sx={{ maxWidth: 150, mx: 'auto' }}>
              <CardMedia
                component="img"
                image={preview}
                alt="Preview"
                sx={{ height: 150, objectFit: 'cover' }}
              />
            </Card>
          </Grid>
        )}

        <Grid item xs={12} textAlign="right">
          <Button type="submit" variant="contained" color="primary">
            üíæ Save Product
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ItemForm;



